package javaPrograms;

public class RelationOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// comparing 2 values are equal or not
		// equal ( ==)
		
		// whenever output is true or false ==> boolean
		
		int a=100;
		int b=200;
		// a==b ==> checking if a is equal to b
		// (a==b)  ==> is called a relational expression
		boolean result = (a==b); // false

		System.out.println(result);  //false
		
		System.out.println("is a and b equal? :" + (a==b)); //false
		
		// not equal to ==> !=
		
		boolean result2= (a!=b);
		
System.out.println(result2);
		
		System.out.println("is a and b equal? :" + (a!=b));
		
		
		
		// >=
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
